const mongoose = require('mongoose');

const GameSchema = new mongoose.Schema({
  title: {
    type: String,
    required: true,
    trim: true
  },
  platform: {
    type: String,
    required: true,
    trim: true
  },
  genre: {
    type: String,
    trim: true
  },
  releaseDate: {
    type: Date
  },
  coverImage: { // URL de la imagen de portada
    type: String,
    trim: true
  },
  status: { // Por ejemplo: 'Pendiente', 'Jugando', 'Completado', 'Abandonado'
    type: String,
    enum: ['Pendiente', 'Jugando', 'Completado', 'Abandonado'],
    default: 'Pendiente'
  },
  score: { // Puntuación de estrellas
    type: Number,
    min: 1,
    max: 5
  },
  hoursPlayed: {
    type: Number,
    default: 0
  },
  user: { // Si quieres asociar juegos a usuarios, necesitarías un modelo de usuario
    type: mongoose.Schema.Types.ObjectId,
    ref: 'User'
  },
  createdAt: {
    type: Date,
    default: Date.now
  }
});

module.exports = mongoose.model('Game', GameSchema);