const mongoose = require('mongoose');

const ReviewSchema = new mongoose.Schema({
  game: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'Game',
    required: true
  },
  user: { // Asociar reseña a un usuario (si tienes autenticación)
    type: mongoose.Schema.Types.ObjectId,
    ref: 'User'
  },
  rating: { // Puntuación de la reseña
    type: Number,
    min: 1,
    max: 5,
    required: true
  },
  comment: { // Reseña detallada
    type: String,
    required: true
  },
  createdAt: {
    type: Date,
    default: Date.now
  }
});

module.exports = mongoose.model('Review', ReviewSchema);