const express = require('express');
const cors = require('cors');
const gameRoutes = require('./routes/gameRoutes');
const reviewRoutes = require('./routes/reviewRoutes'); // Tendrás que crear este

const app = express();

// Middlewares
app.use(cors()); // Permite peticiones de diferentes orígenes (necesario para el frontend)
app.use(express.json()); // Habilita la lectura de JSON en el body de las peticiones

// Rutas
app.use('/api/games', gameRoutes);
app.use('/api/reviews', reviewRoutes); // Define tus rutas para reviews

// Ruta de prueba
app.get('/', (req, res) => {
  res.send('API de GameTracker funcionando!');
});

module.exports = app;