const express = require('express');
const router = express.Router();
const reviewController = require('../controllers/reviewController');

// @route   POST api/reviews
// @desc    Crear una nueva reseña
// @access  Public
router.post('/', reviewController.createReview);

// @route   GET api/reviews
// @desc    Obtener todas las reseñas
// @access  Public
router.get('/', reviewController.getReviews);

// @route   GET api/reviews/:id
// @desc    Obtener una reseña por ID
// @access  Public
router.get('/:id', reviewController.getReviewById);

// @route   GET api/reviews/game/:gameId
// @desc    Obtener todas las reseñas de un juego específico
// @access  Public
router.get('/game/:gameId', reviewController.getReviewsByGameId);

// @route   PUT api/reviews/:id
// @desc    Actualizar una reseña por ID
// @access  Public (se podría restringir con middleware de autenticación/autorización)
router.put('/:id', reviewController.updateReview);

// @route   DELETE api/reviews/:id
// @desc    Eliminar una reseña por ID
// @access  Public (se podría restringir con middleware de autenticación/autorización)
router.delete('/:id', reviewController.deleteReview);

module.exports = router;