import React from 'react';
import './CardJuego.css'; // Crea este archivo CSS para estilos

const CardJuego = ({ game, onEdit, onDelete }) => {
  const renderStars = (score) => {
    return '⭐'.repeat(score) + '☆'.repeat(5 - score);
  };

  return (
    <div className="card-juego">
      <img src={game.coverImage || 'https://via.placeholder.com/150'} alt={game.title} className="card-juego-imagen" />
      <h3>{game.title}</h3>
      <p>Plataforma: {game.platform}</p>
      {game.score && <p>Puntuación: {renderStars(game.score)}</p>}
      <p>Estado: {game.status}</p>
      <div className="card-juego-acciones">
        <button onClick={() => onEdit(game._id)}>Editar</button>
        <button onClick={() => onDelete(game._id)}>Eliminar</button>
      </div>
    </div>
  );
};

export default CardJuego;